import{_ as a,d as t}from"./index-957968eb.js";const d={},e=t('<div class="container" data-v-0349ef38><h3 class="banner-title" style="font-weight:800;text-align:center;padding:150px 0px 50px 0px;" data-v-0349ef38> 亞洲大學-精準健康研究中心 </h3><div class="A" data-aos="zoom-in-up" data-aos-duration="1000" data-v-0349ef38><div class="A-tltie" data-v-0349ef38><div class="A-img" data-v-0349ef38></div></div><div class="A-text" data-v-0349ef38><div class="A-z" data-v-0349ef38> 本中心以精準營養健康為主要產業發展核心，透過智慧健康產業技術研發與人才培育及軟體 平台開發為導向；應用人工智慧、生物資訊、自然語言處理和霎端運算於健康營養產業；整合與分析精準營養大數據、建立混合式雲端健康平台、協助資訊安全等，引領台灣智薏精準營養健康產業及永續城市1社區之發展。 </div><div class="A-y" data-v-0349ef38> 整合醫療大數據與人工智慧，達成精準醫療之目的。精準醫療是未來新的醫療模式，透過醫療大數據之整合基因檢測、疾病治療、日常活動、癒後追蹤等數據，結合人工智慧能協助臨床醫生準確地選擇出適合病人的標靶藥物及相關治療方案，如此一來，不但能降低罹病風險，更可達到預防疾病發生的機會，更有機會提高病人存活率。 本研究中心研究主題包括：應用人工智慧與大數據於• 大數據於基因體分析、表觀基因體分析、微生物體體群分析及抗癌藥物的開發，以及開發精準醫療智慧報告系統。 </div></div></div><hr data-v-0349ef38><h3 class="banner-title" style="font-weight:800;text-align:center;padding:50px 0px 50px 0px;" data-v-0349ef38> 專案負責人 </h3><div class="B" data-v-0349ef38><div class="B-tltie" data-aos="flip-left" data-aos-duration="1000" data-v-0349ef38><div class="card" style="width:20rem;" data-v-0349ef38><div class="BB-img" data-v-0349ef38></div><div class="card-body" data-v-0349ef38><p class="card-text" data-v-0349ef38>王昭能教授</p><p class="card-text" data-v-0349ef38>cnwang@asia.edu.tw</p></div></div></div><div class="B-text" data-aos="flip-right" data-aos-duration="1000" data-v-0349ef38><div class="card" style="width:20rem;" data-v-0349ef38><div class="bb-img" data-v-0349ef38></div><div class="card-body" data-v-0349ef38><p class="card-text" data-v-0349ef38>許聞廉教授</p><p class="card-text" data-v-0349ef38>wenhsu@asia.edu.tw</p></div></div></div></div><hr data-v-0349ef38><h3 class="banner-title" style="font-weight:800;text-align:center;padding:50px 0px 50px 0px;" data-v-0349ef38> 開發人員 </h3><div class="C" style="margin-bottom:20px;" data-v-0349ef38><div class="card" style="width:10rem;" data-aos="zoom-in" data-aos-duration="600" data-v-0349ef38><div class="card-body" style="text-align:center;height:50px;" data-v-0349ef38><p class="card-title" data-v-0349ef38>蔡佳樺</p></div></div><div class="card" style="width:10rem;" data-aos="zoom-in" data-aos-duration="600" data-aos-delay="100" data-v-0349ef38><div class="card-body" style="text-align:center;height:50px;" data-v-0349ef38><p class="card-title" data-v-0349ef38>高淳翊</p></div></div><div class="card" style="width:10rem;" data-aos="zoom-in" data-aos-duration="600" data-aos-delay="200" data-v-0349ef38><div class="card-body" style="text-align:center;height:50px;" data-v-0349ef38><p class="card-title" data-v-0349ef38>陳芷柔</p></div></div></div></div><div class="footer" id="footer" data-v-0349ef38><div class="footer-logo" data-v-0349ef38><p data-v-0349ef38>精準健康研究中心-亞洲大學</p></div></div>',2);function s(i,v){return e}const l=a(d,[["render",s],["__scopeId","data-v-0349ef38"]]);export{l as default};
